template<typename M>
vector<M> FormalPowerSeries<M>::inv(Poly as,int deg){
  assert(as[0]!=M(0));
  Poly rs({M(1)/as[0]});
  for(int i=1;i<deg;i<<=1)
    rs=pre(sub(add(rs,rs),mul(mul(rs,rs),pre(as,i<<1))),i<<1);
  return rs;
}


