template<typename M>
vector<M> FormalPowerSeries<M>::mod(Poly as,Poly bs){
  if(is_zero(as)) return Poly({0});
  shrink(as);shrink(bs);
  as=sub(as,mul(div(as,bs),bs));
  if(is_zero(as)) return Poly({0});
  shrink(as);
  return as;
}

