   PyRun_SimpleString("from imp import reload;");
   PyRun_SimpleString("import initeos;initeos.preinit()");
}

void start_eos() {
   try {
      app().startup();
      wlog("+++++++++++app().startup() done!");
      {

      }
      cv.notify_all();
      init_finished = true;
      app().exec();
   } FC_LOG_AND_DROP();
   wlog("+++++++++++app exec done!!");
   {
       std::lock_guard<std::mutex> lk(cv_m);
       init_finished = true;
       shutdown_finished = true;
   }
   cv.notify_all();
}

void cleos_init();
void init_wallet();
void init_producer();
extern "C" void vm_manager_init_python();
void cleos_set_http_url(std::string& s);

