namespace cv {
namespace gapi {
namespace streaming {

namespace detail {
struct GDesync {
    static const char *id() {
        return "org.opencv.streaming.desync";
    }






    template<typename... R, int... IIs>
    static std::tuple<R...> yield(cv::GCall &call, cv::detail::Seq<IIs...>) {
        return std::make_tuple(cv::detail::Yield<R>::yield(call, IIs)...);
    }
};

template<typename G>
G desync(const G &g) {
    cv::GKernel k{
          GDesync::id()
        , ""
        , [](const GMetaArgs &a, const GArgs &) {return a;}
        , {cv::detail::GTypeTraits<G>::shape}
        , {cv::detail::GTypeTraits<G>::op_kind}
        , {cv::detail::GObtainCtor<G>::get()}
        , {cv::detail::GTypeTraits<G>::op_kind}
    };
    cv::GCall call(std::move(k));
    call.pass(g);
    return std::get<0>(GDesync::yield<G>(call, cv::detail::MkSeq<1>::type()));
}
}


