using asio::ip::tcp;

class SocketClient
{
    public:
    SocketClient()
        : is_running_(false),
          connect_callback_(nullptr),
          disconnect_callback_(nullptr)
    {
    }

    void do_something();

    ~SocketClient()
    {



        if (main_thread_.joinable()) {
            main_thread_.join();
        }
    }

    int init_socket(std::string address, std::string port = "3240")
    {
        socket_.reset(nullptr);

