        mat4 viewproj = proj * view;

        glViewport(0,0,w,h);
        glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT);

        render_world(world, viewproj, solid_shader);



        SDL_GL_SwapWindow(window);
    }

    return 0;
}

