#ifndef UTILS_REDUCTIONS_HPP_
#define UTILS_REDUCTIONS_HPP_

#include <memory>
#include <vector>

#include <config.hpp>
#include <globals.hpp>
#include <kokkos_abstraction.hpp>
#include <parthenon_mpi.hpp>
#include <utils/cleantypes.hpp>
#include <utils/concepts_lite.hpp>
#include <utils/error_checking.hpp>
#include <utils/mpi_types.hpp>

namespace parthenon {




#ifndef MPI_VERSION
enum MPI_Op {
  MPI_MAX,
  MPI_MIN,
  MPI_SUM,
  MPI_PROD,
  MPI_LAND,
  MPI_BAND,
  MPI_LOR,
  MPI_BOR,
  MPI_LXOR,
  MPI_BXOR,
  MPI_MAXLOC,
  MPI_MINLOC
};
#endif

#ifdef MPI_PARALLEL
template <class U>
MPI_Datatype GetContainerMPIType(const U &v) {
  using value_type = decltype(contiguous_container::value_type(v));
  return MPITypeMap<value_type>::type();
}
#endif

template <typename T>
struct ReductionBase {
  T val;
#ifdef MPI_PARALLEL
  MPI_Request req;
  std::shared_ptr<MPI_Comm> pcomm;
#endif
  bool active = false;
  ReductionBase() {
#ifdef MPI_PARALLEL



    pcomm = std::shared_ptr<MPI_Comm>(new MPI_Comm, MPI_Comm_disconnect);
    PARTHENON_MPI_CHECK(MPI_Comm_dup(MPI_COMM_WORLD, pcomm.get()));
#endif
  }

  TaskStatus CheckReduce() {
    if (!active) return TaskStatus::complete;
    int check = 1;
#ifdef MPI_PARALLEL
    PARTHENON_MPI_CHECK(MPI_Test(&req, &check, MPI_STATUS_IGNORE));
#endif
    if (check) {
      active = false;
      return TaskStatus::complete;
    }
    return TaskStatus::incomplete;
  }
};

template <typename T>
struct AllReduce : public ReductionBase<T> {
  TaskStatus StartReduce(MPI_Op op) {
    if (this->active) return TaskStatus::complete;
#ifdef MPI_PARALLEL
    PARTHENON_MPI_CHECK(
        MPI_Iallreduce(MPI_IN_PLACE, contiguous_container::data(this->val),
                       contiguous_container::size(this->val),
                       GetContainerMPIType(this->val), op, *(this->pcomm), &(this->req)));
#endif
    this->active = true;
    return TaskStatus::complete;
  }
};

template <typename T>
struct Reduce : public ReductionBase<T> {
  TaskStatus StartReduce(const int n, MPI_Op op) {
    if (this->active) return TaskStatus::complete;
#ifdef MPI_PARALLEL
    if (Globals::my_rank == n) {
      PARTHENON_MPI_CHECK(MPI_Ireduce(MPI_IN_PLACE, contiguous_container::data(this->val),
                                      contiguous_container::size(this->val),
                                      GetContainerMPIType(this->val), op, n,
                                      *(this->pcomm), &(this->req)));

    } else {
      PARTHENON_MPI_CHECK(MPI_Ireduce(contiguous_container::data(this->val), nullptr,
                                      contiguous_container::size(this->val),
                                      GetContainerMPIType(this->val), op, n,
                                      *(this->pcomm), &(this->req)));
    }
#endif
    this->active = true;
    return TaskStatus::complete;
  }
};

}

#endif

