class CookieIface;
class TaskQueue;
class CB3ExecutorThread;

using ThreadQ = std::vector<std::unique_ptr<CB3ExecutorThread>>;
using TaskQpair = std::pair<ExTask, TaskQueue*>;
using TaskQ = std::vector<TaskQueue*>;

class CB3ExecutorPool : public ExecutorPool {
public:

    CB3ExecutorPool(size_t maxThreads,
                    ThreadPoolConfig::ThreadCount maxReaders,
                    ThreadPoolConfig::ThreadCount maxWriters,
                    ThreadPoolConfig::AuxIoThreadCount maxAuxIO,
                    ThreadPoolConfig::NonIoThreadCount maxNonIO,
                    ThreadPoolConfig::IOThreadsPerCore ioThreadsPerCore);

    ~CB3ExecutorPool() override;

    void addWork(size_t newWork, TaskType qType);

    void lessWork(TaskType qType);

    void startWork(TaskType taskType);

    void doneWork(TaskType taskType);

    bool trySleep(TaskType task_type) {
        if (!numReadyTasks[static_cast<size_t>(task_type)]) {
            numSleepers++;
            return true;
        }
        return false;
    }

    void woke() {
        numSleepers--;
    }

    TaskQueue* nextTask(CB3ExecutorThread& t, uint8_t tick);

    TaskQueue* getSleepQ(TaskType curTaskType) {
        return isHiPrioQset ? hpTaskQ[static_cast<size_t>(curTaskType)]
                            : lpTaskQ[static_cast<size_t>(curTaskType)];
    }

    bool cancel(size_t taskId, bool remove = false) override;

    bool wakeAndWait(size_t taskId) override;


    void adjustWorkers(TaskType type, size_t newCount);

    bool snoozeAndWait(size_t taskId, double tosleep) override;

    void registerTaskable(Taskable& taskable) override;

    void unregisterTaskable(Taskable& taskable, bool force) override;

    size_t getNumTaskables() const override {
        return numTaskables;
    }

    void doWorkerStat(Taskable& taskable,
                      CookieIface& cookie,
                      const AddStatFn& add_stat) override;


    void doTasksStat(Taskable& taskable,
                     CookieIface& cookie,
                     const AddStatFn& add_stat) override;

    void doTaskQStat(Taskable& taskable,
                     CookieIface& cookie,
                     const AddStatFn& add_stat) override;

    size_t getNumWorkersStat() const override {
        std::lock_guard<std::mutex> lh(tMutex);
        return threadQ.size();
    }

    size_t getNumReaders() const override;

    size_t getNumWriters() const override;

    size_t getNumAuxIO() const override;

    size_t getNumNonIO() const override;

    std::string_view getName() const override {
        return "CB3";
    }

    void setNumReaders(ThreadPoolConfig::ThreadCount v) override {
        adjustWorkers(TaskType::Reader, calcNumReaders(v));
    }

    void setNumReadersExactly(uint16_t v) override {
        adjustWorkers(TaskType::Reader, v);
    }

    size_t getNumReadersExactly() const override {
        return numWorkers[static_cast<size_t>(TaskType::Reader)].load();
    }

    void setNumWriters(ThreadPoolConfig::ThreadCount v) override {
        adjustWorkers(TaskType::Writer, calcNumWriters(v));
    }

    void setNumAuxIO(ThreadPoolConfig::AuxIoThreadCount v) override {
        adjustWorkers(TaskType::AuxIO, calcNumAuxIO(v));
    }

    void setNumNonIO(ThreadPoolConfig::NonIoThreadCount v) override {
        adjustWorkers(TaskType::NonIO, calcNumNonIO(v));
    }

    size_t getNumReadyTasks() const override {
        return totReadyTasks;
    }

    size_t getNumSleepers() const override {
        return numSleepers;
    }

    size_t schedule(ExTask task) override;

protected:
    TaskQueue* _nextTask(CB3ExecutorThread& t, uint8_t tick);


    ExTask _cancel(size_t taskId, bool remove = false);

    bool _wake(size_t taskId);
    virtual bool _startWorkers();


    void _adjustWorkers(TaskType type, size_t desiredNumItems);

    bool _snooze(size_t taskId, double tosleep);
    size_t _schedule(ExTask task);
    void _registerTaskable(Taskable& taskable);
    void _unregisterTaskable(Taskable& taskable, bool force);
    std::vector<ExTask> _stopTaskGroup(task_gid_t taskGID,
                                       std::unique_lock<std::mutex>& lh,
                                       bool force);
    TaskQueue* _getTaskQueue(const Taskable& t, TaskType qType);
    void _stopAndJoinThreads();

    const size_t numTaskSets{static_cast<size_t>(TaskType::Count)};

    std::atomic<size_t> totReadyTasks;
    SyncObject mutex;


    std::map<size_t, TaskQpair> taskLocator;


    ThreadQ threadQ;


    TaskQ hpTaskQ;
    std::atomic_bool isHiPrioQset;

    TaskQ lpTaskQ;
    std::atomic_bool isLowPrioQset;


    size_t numTaskables;


    mutable SyncObject tMutex;

    std::atomic<uint16_t> numSleepers;
    std::vector<std::atomic<uint16_t>>
            curWorkers;

    std::vector<std::atomic<int>> numWorkers;
    std::vector<std::atomic<size_t>>
            numReadyTasks;


    std::set<void*> taskOwners;


    friend class ExecutorPool;
};

