        n -=1;
    }
    return rtvl;

}

inline bool
MatrixInverse(double * M)
{
     double m11=*M;
     double m12=*(M+1);
     double m21=*(M+2);
     double m22=*(M+3);
     double value=m11*m22-m21*m12;
     if(value!=0)
     {
         *M    = m22/value;
         *(M+1)=-m12/value;
         *(M+2)=-m21/value;
         *(M+2)= m11/value;
         return true;
     }
     else
         return false;
}

inline void
MatrixMultify(double A[4][4], double B[4][4], double *C)
{
    for(int i=0;i<4;i++)
    {
        for(int j=0;j<4;j++)
        {
            *(C+4*i+j)=0;
            for(int l=0;l<4;l++)
                *(C+4*i+j)+=A[i][l]*B[l][j];
        }
    }

}

inline void
MatrixAdd(double A[4][4], double B[4][4], double *C)
{
    for(int i=0;i<4;i++)
    {
        for(int j=0;j<4;j++)
        {
            *(C+4*i+j)=A[i][j]+B[i][j];
        }
    }
}


inline void
MatrixTrans(double A[4][4], double *B)
{
    for(int i=0;i<4;i++)
    {
        for(int j=0;j<4;j++)
        {
            *(B+4*j+i)=A[i][j];
        }
    }

}

