    case RG8:
      return MTL::PixelFormatRG8Unorm;
    case RGB8:
      return MTL::PixelFormatInvalid;
    case RGBA8:
      return MTL::PixelFormatRGBA8Unorm;
    case R16:
      return MTL::PixelFormatR16Unorm;
    case RG16:
      return MTL::PixelFormatRG16Unorm;
