      return MTL::PixelFormatR32Uint;
    case RG32U:
      return MTL::PixelFormatRG32Uint;
    case RGB32U:
      return MTL::PixelFormatInvalid;
    case RGBA32U:
      return MTL::PixelFormatRGBA32Uint;
    case Depth16:
      return MTL::PixelFormatDepth16Unorm;
    case Depth24x8:
      return MTL::PixelFormatInvalid;
    case Depth24S8:
      return MTL::PixelFormatDepth24Unorm_Stencil8;
    case Depth32F:
