inline MTL::CullMode nativeFormat(RenderState::CullMode m) {
  switch(m) {
    case RenderState::CullMode::NoCull:
      return MTL::CullModeNone;
    case RenderState::CullMode::Back:
      return MTL::CullModeBack;
    case RenderState::CullMode::Front:
      return MTL::CullModeFront;
    }
  return MTL::CullModeNone;
  }

inline MTL::PrimitiveType nativeFormat(Topology t) {
  switch(t) {
    case Topology::Points:    return MTL::PrimitiveTypePoint;
    case Topology::Lines:     return MTL::PrimitiveTypeLine;
    case Topology::Triangles: return MTL::PrimitiveTypeTriangle;
    }
  return MTL::PrimitiveTypePoint;
  }

inline MTL::IndexType nativeFormat(IndexClass icls) {
  switch(icls) {
    case IndexClass::i16: return MTL::IndexTypeUInt16;
    case IndexClass::i32: return MTL::IndexTypeUInt32;
    }
  return MTL::IndexTypeUInt16;
  }

inline MTL::AccelerationStructureInstanceOptions nativeFormat(RtInstanceFlags f) {
  MTL::AccelerationStructureInstanceOptions ret = 0;
  if((f & RtInstanceFlags::NonOpaque)==RtInstanceFlags::NonOpaque)
    ret |= MTL::AccelerationStructureInstanceOptionNonOpaque; else
    ret |= MTL::AccelerationStructureInstanceOptionOpaque;
  if((f & RtInstanceFlags::CullDisable)==RtInstanceFlags::CullDisable)
    ret |= MTL::AccelerationStructureInstanceOptionDisableTriangleCulling;
  if((f & RtInstanceFlags::CullFlip)==RtInstanceFlags::CullFlip)
    ret |= MTL::AccelerationStructureInstanceOptionTriangleFrontFacingWindingCounterClockwise;
  return ret;
  }

inline MTL::RenderStages nativeFormat(ShaderReflection::Stage st) {
  uint32_t stageFlags = 0;
  if(st&ShaderReflection::Compute)
    stageFlags |= 0;
  if(st&ShaderReflection::Vertex)
    stageFlags |= MTL::RenderStageVertex;
  if(st&ShaderReflection::Control)
    stageFlags |= 0;
  if(st&ShaderReflection::Evaluate)
    stageFlags |= 0;
  if(st&ShaderReflection::Geometry)
    stageFlags |= 0;
  if(st&ShaderReflection::Fragment)
    stageFlags |= MTL::RenderStageFragment;
  if(st&ShaderReflection::Task)
    stageFlags |=  MTL::RenderStageObject;
  if(st&ShaderReflection::Mesh)
    stageFlags |=  MTL::RenderStageMesh;
  return MTL::RenderStages(stageFlags);
  }

