            if (!read_u32(data, data_len, &v))
                return false;

            if (v <= 0xffff)
                return false;

            *value = (uint64_t)v;
            break;
        }
        case 0xfd: {
            uint16_t v;

            if (!read_u16(data, data_len, &v))
                return false;

            if (v < 0xfd)
                return false;

            *value = (uint64_t)v;
