				db_ = createBackend2(ss.str(), kb_);
				reasoner_ = createReasoner2(ss.str(), kb_, db_);

				kb_->loadCommon();
				kb_->init();
			}
			return reasoner_;
		}

		static std::string getPath(const std::string &filename) {
			return std::filesystem::path("reasoner") / "mongolog" / filename;
		}
