			DC_TRY(Ctx.Writer->WriteNone());
	}
	else
	{
		return DC_FAIL(DcDReadWrite, PropertyMismatch)
			<< TEXT("GameplayTag") << Datum.Property.GetFName() << Datum.Property.GetClassName();
