void PlannerManager::init(ros::NodeHandle &nh, ros::NodeHandle &nh_prev)
{

  debug_publisher::DBSendNew("plan_manager", "planner_manager init start");
  config.loadParameters(nh_prev);
  Loadstartandend = config.loadStartEnd;
