import { Renderer, RendererOptions } from "./renderer";
import EventEmitter from "events";

export type ScratcherEventName = "start" | "move" | "end";
export type ScratcherEvent = {
  type: ScratcherEventName;
  x: number;
  y: number;
};
export type ScratcherEventHandler = (event: ScratcherEvent) => void;
export type ScratcherOptions = RendererOptions & {
  brushSize: number;
};

export abstract class Scratcher {
  protected readonly renderer: Renderer;
  protected readonly brushSize: number;
  public readonly events: EventEmitter;

  constructor(options: ScratcherOptions) {
    this.renderer = new Renderer(options);
    this.brushSize = options.brushSize;
    this.events = new EventEmitter();
  }

  public async render() {
    await this.renderer.render();
  }

  public destroy() {
    this.renderer.destroy();
  }

  protected abstract onStart(x: number, y: number): void;
  protected abstract onMove(x: number, y: number): void;
  protected abstract onEnd(x: number, y: number): void;
}

export class MouseScratcher extends Scratcher {
  private isScratching = false;

  constructor(options: ScratcherOptions) {
    super(options);
    this.renderer.canvas.addEventListener("mousedown", this.handleMouseDown);
    this.renderer.canvas.addEventListener("mousemove", this.handleMouseMove);
    this.renderer.canvas.addEventListener("mouseup", this.handleMouseUp);
  }

  private handleMouseDown = (event: MouseEvent) => {
    this.isScratching = true;
    this.onStart(event.offsetX, event.offsetY);
  };

  private handleMouseMove = (event: MouseEvent) => {
    if (this.isScratching) {
      this.onMove(event.offsetX, event.offsetY);
    }
  };

  private handleMouseUp = (event: MouseEvent) => {
    this.isScratching = false;
    this.onEnd(event.offsetX, event.offsetY);
  };

  protected onStart(x: number, y: number) {
    this.renderer.circle(x, y, this.brushSize);
    this.events.emit("start", { type: "start", x, y });
  }

  protected onMove(x: number, y: number) {
    this.renderer.circle(x, y, this.brushSize);
    this.events.emit("move", { type: "move", x, y });
  }

  protected onEnd(x: number, y: number) {
    this.events.emit("end", { type: "end", x, y });
  }
}

export class TouchScratcher extends Scratcher {
  private isScratching = false;

  constructor(options: ScratcherOptions) {
    super(options);
    this.renderer.canvas.addEventListener("touchstart", this.handleTouchStart);
    this.renderer.canvas.addEventListener("touchmove", this.handleTouchMove);
    this.renderer.canvas.addEventListener("touchend", this.handleTouchEnd);
  }

  private handleTouchStart = (event: TouchEvent) => {
    this.isScratching = true;
    const { offsetX, offsetY } = event.touches[0];
    this.onStart(offsetX, offsetY);
  };

  private handleTouchMove = (event: TouchEvent) => {
    if (this.isScratching) {
      const { offsetX, offsetY } = event.touches[0];
      this.onMove(offsetX, offsetY);
    }
  };

  private handleTouchEnd = (event: TouchEvent) => {
    this.isScratching = false;
    const { offsetX, offsetY } = event.changedTouches[0];
    this.onEnd(offsetX, offsetY);
  };

  protected onStart(x: number, y: number) {
    this.renderer.circle(x, y, this.brushSize);
    this.events.emit("start", { type: "start", x, y });
  }

  protected onMove(x: number, y: number) {
    this.renderer.circle(x, y, this.brushSize);
    this.events.emit("move", { type: "move", x, y });
  }

  protected onEnd(x: number, y: number) {
    this.events.emit("end", { type: "end", x, y });
  }
}

