    const firstFlush = [{ ts: 0, key: "a" }];
    const secondFlush = [
        { ts: 0, key: "a" },
        { ts: 1, key: "b" },
    ];
    const thirdFlush = [
        { ts: 0, key: "a" },
        { ts: 1, key: "b" },
        { ts: 2, key: "c" },
    ];
    const fourthFlush = [
        { ts: 1, key: "b" },
        { ts: 2, key: "c" },
        { ts: 4, key: "d" },
    ];

    const flushes = [firstFlush, secondFlush, thirdFlush, fourthFlush];
    return new Promise((resolve, reject) => {
        source
            .pipe(
                accumulator(FlushStrategy.sliding, 3, undefined, {
                    objectMode: true,
                }),
