        });
        source.push(null);
    });
});

test("accumulator() sliding with key", (t) => {
    t.plan(6);
    let chunkIndex = 0;
    interface TestObject {
        ts: number;
        key: string;
    }
    const source = new Readable({ objectMode: true });
    const input = [
        { ts: 0, key: "a" },
        { ts: 1, key: "b" },
        { ts: 2, key: "c" },
        { ts: 3, key: "d" },
        { ts: 5, key: "f" },
        { ts: 6, key: "g" },
    ];
    const firstFlush = [{ ts: 0, key: "a" }];
    const secondFlush = [
        { ts: 0, key: "a" },
        { ts: 1, key: "b" },
    ];
    const thirdFlush = [
        { ts: 0, key: "a" },
        { ts: 1, key: "b" },
        { ts: 2, key: "c" },
    ];
    const fourthFlush = [
        { ts: 1, key: "b" },
        { ts: 2, key: "c" },
        { ts: 3, key: "d" },
    ];
    const fifthFlush = [
        { ts: 3, key: "d" },
        { ts: 5, key: "f" },
    ];
    const sixthFlush = [
        { ts: 5, key: "f" },
        { ts: 6, key: "g" },
    ];

    const flushes = [
        firstFlush,
        secondFlush,
        thirdFlush,
        fourthFlush,
        fifthFlush,
        sixthFlush,
    ];
    return new Promise((resolve, reject) => {
        source
            .pipe(
                accumulator(FlushStrategy.sliding, 3, "ts", {
                    objectMode: true,
                }),
            )
            .on("data", (flush: TestObject[]) => {
                t.deepEqual(flush, flushes[chunkIndex]);
                chunkIndex++;
            })
            .on("error", reject)
            .on("end", resolve);
        input.forEach((item) => {
            source.push(item);
        });
        source.push(null);
    });
});

test("accumulator() sliding should emit error and ignore chunk when key is missing", (t) => {
    t.plan(2);
    let index = 0;
    interface TestObject {
        ts: number;
        key: string;
    }
    const source = new Readable({ objectMode: true });
    const accumulatorStream = accumulator(
        FlushStrategy.sliding,
        3,
        "nonExistingKey",
        { objectMode: true },
    );
    const input = [
        { ts: 0, key: "a" },
        { ts: 1, key: "b" },
    ];
    return new Promise((resolve, reject) => {
        source
            .pipe(accumulatorStream)
            .on("data", (flush: TestObject[]) => {
                expect(flush).to.deep.equal([]);
            })
            .on("error", (err: any) => {
                source.pipe(accumulatorStream);
                accumulatorStream.resume();
                expect(err.message).to.equal(
                    `Key is missing in event: (nonExistingKey, ${JSON.stringify(
                        input[index],
                    )})`,
                );
                index++;
                t.pass();
            })
            .on("end", resolve);
        input.forEach((item) => {
            source.push(item);
        });
        source.push(null);
    });
});

