import * as React from 'react'
import { createClient } from '@supabase/supabase-js'
import { SupabaseContextType } from './types'

const SupabaseContext = React.createContext<SupabaseContextType | undefined>(undefined)

interface SupabaseProviderProps {
  supabaseUrl: string
  supabaseKey: string
  children: React.ReactNode
}

const SupabaseProvider: React.FC<SupabaseProviderProps> = ({ supabaseUrl, supabaseKey, children }) => {
  const [sb, setSb] = React.useState<SupabaseClient | null>(null)
  const [user, setUser] = React.useState<User | null>(null)

  React.useEffect(() => {
    const supabase = createClient(supabaseUrl, supabaseKey)
    setSb(supabase)

    const { data: authListener } = supabase.auth.onAuthStateChange((event, session) => {
      setUser(session?.user || null)
    })

    return () => {
      authListener?.unsubscribe()
    }
  }, [supabaseUrl, supabaseKey])

  return (
    <SupabaseContext.Provider value={{ sb, user }}>
      {children}
    </SupabaseContext.Provider>
  )
}

