export default openApiDocument as OpenApiDocument;

#Modify
async function resolveExternalReference(ref) {
  const { protocol } = new URL(ref);
  if (protocol === 'file:') {
    const filePath = ref.replace('file://', '');
    return jsYaml.load(fs.readFileSync(filePath, 'utf-8'));
  } else if (protocol === 'http:' || protocol === 'https:') {
    const response = await axios.get(ref);
    return response.data;
  }
  throw new Error(`Unsupported protocol: ${protocol}`);
}

export async function resolveReference(document, reference) {
  const { $ref } = reference;
  if ($ref.startsWith('#')) {
    const path = $ref.substring(2).split('/');
    return path.reduce((doc, key) => doc[key], document);
  } else {
    const externalDocument = await resolveExternalReference($ref);
    return resolveReference(externalDocument, { $ref: '#' });
  }
}

