export const calculatePercentage = (percentOf: string, over: string, decimal: number): number => {
  const percentOfValue = parseFloat(percentOf);
  const overValue = parseFloat(over);
  if (isNaN(percentOfValue) || isNaN(overValue)) {
    return 0;
  }
  return parseFloat(((percentOfValue / overValue) * 100).toFixed(decimal));
};

