getTimeZoneFromTopology(abbreviation: string, offset: number, timezoneAbbrMappings: TimeZoneAbbrMapping[]): string {
    if (abbreviation === null || offset === null) {
        return null;
    }
    if (/^[+-]\d{2}$/.test(abbreviation)) {
        return `Etc/GMT${abbreviation}`;
    }
    const timezone = timezoneAbbrMappings.find(tz => tz.abbreviation === abbreviation && tz.offset === offset);
    return timezone ? timezone.timezone : null;
}

