:import { css } from 'lit';

export const baseCss = css`
  * {
    box-sizing: border-box;
    overflow-wrap: break-word;
    word-wrap: break-word;
    word-break: break-word;
  }

  h1 {
    text-align: center;
  }

  h1,
  h2 {
    font-weight: normal;
    margin: 0;
  }

  img {
    max-width: 100%;
  }

  p {
    margin: 0;
  }

  p + p {
    margin-top: 1em;
  }

  code {
    font-size: 1.125em;
    font-family: monospace;
  }

  a {
    color: var(--link-color);
  }

  .region a {
    font-weight: bold;
  }

  button {
    appearance: none;
    padding: 0;
    margin: 0;
    border: 0;
    border-radius: 0;
    font: inherit;
    line-height: inherit;
    background: transparent;
    display: inline-flex;
    min-height: 48px;
    min-width: 48px;
    place-items: center;
    place-content: center;
    cursor: pointer;
  }

  textarea,
  input {
    font: inherit;
    line-height: inherit;
    width: 100%;
  }

  .textarea,
  .input {
    padding: .5em;
    border: 1px solid;
    border-radius: 0;
    font-size: .875em;
    font-weight: normal;
    background-color: var(--dark-background-color);
    color: var(--text-on-dark-background-color);
  }

  .button {
    background-color: var(--light-background-color);
    color: var(--text-on-light-background-color);
    border-radius: 0;
    font-size: 1.125em;
    padding: .5em .875em;
    max-width: max-content;
    border: 1px solid;
  }

  .button--tag {
    font-size: .95em;
    padding: 4px 8px;
    min-height: 32px;
    min-width: 32px;
  }

  .button--cta {
    font-size: 1.5em;
    font-weight: bold;
    background: var(--accent-color);
    color: var(--text-on-accent-color);
  }

  .label {
    display: flex;
    flex-direction: column;
    font-size: 1.25em;
    padding-bottom: 12px;
    border-bottom: 1px solid;
    margin-bottom: 12px;
  }

  .label-text {
    display: block;
    padding: 6px 0;
  }

  .error-message {
    color: var(--error-color);
    font-size: .875em;
    display: block;
    padding: 6px;
    font-weight: bold;
  }

  .hint-text {
    padding: 6px;
    font-size: .75em;
  }

  .toggle-button {
    width: auto;
    appearance: none;
    background: gray;
    width: 60px;
    height: 30px;
    border-radius: 0;
    position: relative;
  }

  .toggle-button:after {
    content: '';
    background: var(--primary-color);
    position: absolute;
    top: 4px;
    left: 4px;
    height: calc(100% - 8px);
    width: calc(50% - 8px);
    border-radius: 0;
  }

  .toggle-button:checked {
    background: var(--accent-color);
  }

  .toggle-button:checked:after {
    left: unset;
    right: 4px;
  }

  .regions {
    display: grid;
  }

  .region {
    padding: 32px;
    border-radius: 0;
    background-color: var(--dark-background-color);
    color: var(--text-on-dark-background-color);
    width: 100%;
    border: 1px solid;
    font-size: 1.125em;
  }

  .eyebrow {
    text-transform: uppercase;
    font-size: .75em;
    letter-spacing: .1ch;
  }

  .avatar {
    overflow: hidden;
    display: inline-flex;
  }

  .avatar--small {
    border-radius: 0;
    height: 70px;
    width: 70px;
    background: var(--accent-color);
  }

  .permalink {
    display: block;
    font-size: .75em;
    text-align: center;
  }

  .actor-lockup {
    display: inline-flex;
    flex-direction: column;
    text-align: center;
    place-items: center;
    place-content: center;
    border: 1px solid lightgray;
    border-radius: 0;
    padding: 8px;
    aspect-ratio: 1;
  }

  .actor-lockup:not(:hover) {
    text-decoration: none;
  }

  .actor-username {
    display: block;
    color: var(--medium-background-color);
    font-weight: bold;
  }

  .actor-hostname {
    display: block;
    font-size: .75em;
  }
`;
'ap-groups/components/group-entity-details.ts'
:import {LitElement, html, css, nothing} from 'lit';
import {customElement, property, query} from 'lit/decorators';
import { repeat } from 'lit/directives/repeat';
import { baseCss } from './base-css';
import { AP } from 'activitypub-core-types';
import { unsafeHTML } from 'lit/directives/unsafe-html';

@customElement('group-entity-details')
export class GroupEntityDetails extends LitElement {
  static styles = [baseCss, css`
    :host {
      display: block;
    }
    .avatar {
      max-width: 200px;
    }
  `];

  @property({type: String, attribute: 'entity-id'})
  private entityId?: string;

  @property({ type: Object })
  private entity: AP.Actor | null = null;

  @property({ type: Boolean })
  private isDeleted = false;

  override firstUpdated() {
    const url = new URL(this.entityId);
    const isLocal = url.hostname === window.location.hostname;

    fetch(isLocal ? this.entityId : `/proxy?resource=${this.entityId}`, {
      headers: {
        'Accept': 'application/activity+json'
      }
    })
      .then(res => res.json())
      .then(entity => {
        this.entity = entity;
      })
      .catch(() => {
        this.isDeleted = true;
      });
  }


  render() {
    if (this.isDeleted) {
      return html`
        Deleted.
      `;
    }

    if (!this.entity) {
      return html`
        Loading...
      `;
    }

    return html`
      <dl role="table">
        <div role="row">
          <dt>
            Avatar
          </dt>
          <dd>
            ${(this.entity.icon && 'url' in this.entity.icon) ? html`
              <a href=${this.entity.url}>
                <img
                  class="avatar"
                  src=${this.entity.icon.url}
                />
              </a>
            ` : nothing}
          </dd>
        </div>
        <div role="row">
          <dt>
            Group Name
          </dt>
          <dd>
            <a href=${this.entity.url}>
              ${this.entity.name}
            </a>
          </dd>
        </div>
        <div role="row">
          <dt>
            18+ / Sensitive / NSFW?
          </span>
          <dd>
            ${this.entity.sensitive ? 'Yes' : 'No'}
          </dd>
        </div>
        <div role="row">
          <dt>
            Group Description
          </span>
          <dd>
            ${unsafeHTML(this.entity.summary ?? '')}
          </span>
        </div>
        ${Array.isArray(this.entity.attachment) ? repeat(this.entity.attachment, attachment => {
          const propertyValue = attachment as unknown as {
            name: string;
            value: string;
          };

          return html`
            <div role="row">
              <dt>
                ${propertyValue.name}
              </dt>
              <dd>
                ${unsafeHTML(propertyValue.value)}
              </dd>
            </div>
          `;
        }) : nothing}
        <div role="row">
          <dt>
            Manually Approves Members
          </dt>
          <dd>
            ${this.entity.manuallyApprovesFollowers ? 'Yes' : 'No'}
          </dd>
        </div>
      </dl>
    `;
  }
}

declare global {
  interface HTMLElementTagNameMap {
    "group-entity-details": GroupEntityDetails;
  }
}

