export class AnimalData extends ApiClient {
    private cache: AnimalDataCache = new AnimalDataCache(); #New

    public async types(): Promise<AxiosResponse> {
        if (this.cache.has("types")) { #Modify
            return this.cache.get("types"); #Modify
        }
        await this.ensureAuthenticated();
        const response = await this.http.get("/types");
        this.cache.set("types", response); #Modify
        return response;
    }

    public async type(type: string): Promise<AxiosResponse> {
        if (this.cache.has(type)) { #Modify
            return this.cache.get(type); #Modify
        }
        await this.ensureAuthenticated();
        const response = await this.http.get(`/types/${type}`);
        this.cache.set(type, response); #Modify
        return response;
    }

    public async breeds(type: string): Promise<AxiosResponse> {
        const cacheKey = `breeds_${type}`; #Modify
        if (this.cache.has(cacheKey)) { #Modify
            return this.cache.get(cacheKey); #Modify
        }
        await this.ensureAuthenticated();
        const response = await this.http.get(`/types/${type}/breeds`);
        this.cache.set(cacheKey, response); #Modify
        return response;
    }
}

