'scratchable/src/renderer.ts'
:export interface RendererOptions {
  container: HTMLElement;
  background:
    | { type: "single"; color: string }
    | {
        type: "linear-gradient";
        gradients: { offset: number; color: string }[];
      }
    | { type: "image"; url: string };
}

export class Renderer {
  private readonly container: HTMLElement;
  private readonly canvas: HTMLCanvasElement;
  private readonly ctx: CanvasRenderingContext2D;
  private readonly background: RendererOptions["background"];

  constructor(options: RendererOptions) {
    this.container = options.container;
    this.canvas = document.createElement("canvas");
    this.ctx = this.canvas.getContext("2d")!;
    this.background = options.background;
  }

  public get dpr() {
    return window.devicePixelRatio;
  }

  public get percentage() {
    const { data } = this.ctx.getImageData(0, 0, this.canvas.width, this.canvas.height);
    const stride = 32;
    const totalPixels = data.length / stride;
    let scratchedPixels = 0;

    for (let i = 0; i < data.length; i += stride) {
      if (data[i] === 0) {
        scratchedPixels++;
      }
    }
    return scratchedPixels / totalPixels;
  }

  public async render() {
    this.container.style.position = "relative";

    const dpr = window.devicePixelRatio;
    const canvasWidth = this.container.offsetWidth * dpr;
    const canvasHeight = this.container.offsetHeight * dpr;

    this.canvas.width = canvasWidth;
    this.canvas.height = canvasHeight;

    this.canvas.style.width = "100%";
    this.canvas.style.height = "100%";
    this.canvas.style.position = "absolute";
    this.canvas.style.top = "0";
    this.canvas.style.left = "0";
    this.canvas.style.right = "0";
    this.canvas.style.bottom = "0";

    return new Promise<void>((resolve) => {
      const renderCanvas = () => {
        this.ctx.globalCompositeOperation = "destination-out";
        this.container.appendChild(this.canvas);
        resolve();
      };

      switch (this.background.type) {
        case "single": {
          this.ctx.fillStyle = this.background.color;
          this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
          renderCanvas();
          return;
        }
        case "linear-gradient": {
          const grd = this.ctx.createLinearGradient(0, 0, this.canvas.width, this.canvas.height);

          this.background.gradients.forEach((gradient) => {
            grd.addColorStop(gradient.offset, gradient.color);
          });

          this.ctx.fillStyle = grd;
          this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
          renderCanvas();
          return;
        }
        case "image": {
          const image = new Image();

          image.onload = () => {
            this.ctx.drawImage(image, 0, 0, this.canvas.width, this.canvas.height);
            renderCanvas();
          };

          image.src = this.background.url;
          return;
        }
      }
    });
  }

  public circle(x: number, y: number, radius: number) {
    const dpr = this.dpr;

    this.ctx.beginPath();
    this.ctx.arc(x * dpr, y * dpr, radius * dpr, 0, 2 * Math.PI);
    this.ctx.fill();
  }

  public line(from: { x: number; y: number }, to: { x: number; y: number }, width: number) {
    const dpr = this.dpr;

    this.ctx.beginPath();
    this.ctx.moveTo(from.x * dpr, from.y * dpr);
    this.ctx.lineTo(to.x * dpr, to.y * dpr);
    this.ctx.lineWidth = width * dpr;
    this.ctx.stroke();
  }

  public destroy() {
    this.container.removeChild(this.canvas);
  }
}

'scratchable/src/scratcher.ts'
:import { Events } from "./events";
import { Renderer, RendererOptions } from "./renderer";

export type ScratcherEventName = "scratch";

export interface ScratcherEvent {
  percentage: number;
}

export type ScratcherEventHandler = (e: ScratcherEvent) => void;

export interface ScratcherOptions extends RendererOptions {
  container: HTMLElement;
  radius?: number;
  onScratch?: ScratcherEventHandler;
}

export class Scratcher {
  protected readonly container: HTMLElement;
  private readonly renderer: Renderer;
  private readonly radius: number;
  public readonly events: Events<ScratcherEventName, ScratcherEvent>;

  private prevScratchPosition: { x: number; y: number } | null = null;

  constructor(options: ScratcherOptions) {
    this.container = options.container;
    this.renderer = new Renderer(options);
    this.radius = options.radius ?? 50;
    this.events = new Events(() => ({ percentage: this.renderer.percentage }));

    if (options.onScratch != null) {
      this.events.on("scratch", options.onScratch);
    }
  }

  protected move(x: number, y: number) {
    this.renderer.circle(x, y, this.radius);

    if (this.prevScratchPosition != null) {
      this.renderer.line(this.prevScratchPosition, { x, y }, this.radius * 2);
    }

    this.prevScratchPosition = { x, y };
    this.events.emit("scratch");
  }

  protected end() {
    this.prevScratchPosition = null;
  }

  public async render() {
    await this.renderer.render();
  }

  public destroy() {
    this.renderer.destroy();
    this.events.purge();
  }
}

export class TouchScratcher extends Scratcher {
  constructor(options: ScratcherOptions) {
    super(options);
    this.container.style.touchAction = "none";
  }

  private touchmove = (e: TouchEvent) => {
    const { left, top } = this.container.getBoundingClientRect();
    const { clientX, clientY } = e.changedTouches[0];

    const x = clientX - left;
    const y = clientY - top;
    this.move(x, y);
  };

  private touchend = () => {
    this.end();
  };

  public async render() {
    this.container.addEventListener("touchmove", this.touchmove);
    this.container.addEventListener("touchend", this.touchend);
    await super.render();
  }

  public destory() {
    this.container.removeEventListener("touchmove", this.touchmove);
    this.container.removeEventListener("touchend", this.touchend);
    super.destroy();
  }
}

export class MouseScratcher extends Scratcher {
  constructor(options: ScratcherOptions) {
    super(options);
  }

  private mousemove = (e: MouseEvent) => {
    if (e.buttons === 0) {
      this.end();
      return;
    }

    const { left, top } = this.container.getBoundingClientRect();
    const { clientX, clientY } = e;

    const x = clientX - left;
    const y = clientY - top;
    this.move(x, y);
  };

  public async render() {
    this.container.addEventListener("mousemove", this.mousemove);
    await super.render();
  }

  public destroy() {
    this.container.removeEventListener("mousemove", this.mousemove);
    super.destroy();
  }
}

