export class AuthGuard implements CanActivate {

    constructor(private router: Router) {
    }

    canActivate(route: ActivatedRouteSnapshot, state: RouterStateSnapshot): boolean {
        const requiredRole = route.data['role'];
        const token = sessionStorage.getItem('app.token');
        const roles = sessionStorage.getItem('app.roles');
        const timeout = sessionStorage.getItem('app.timeout');

        // #Modify: Check if the timeout has been reached
        if (timeout && Date.now() > parseInt(timeout)) {
            sessionStorage.removeItem('app.token');
            sessionStorage.removeItem('app.roles');
            sessionStorage.removeItem('app.timeout');
            this.router.navigate(['/login']);
            return false;
        }

        if (token && roles) {
            const userRoles = roles.split(' ');
            if (userRoles.includes(requiredRole)) {
                return true;
            }
        }

        this.router.navigate(['/login']);
        return false;
    }
}

