interface NavContext {
  setShowSearchBox?: any;
}
const NavContext = createContext<NavContext>({});
export function useNavContext() {
  return useContext(NavContext);
}

export const Nav = ({ children }) => {
  let { wallet, contract } = useNear();
  let { errorList } = useErrors();
  let [showErrorBox, setShowErrorBox] = useState(true);
  let [showSearchBox, setShowSearchBox] = useState(false);
