'layout/src/controllers/StyleController.ts'
:import { NineSliceSprite, TilingSprite } from 'pixi.js';
import { LayoutSystem } from '../Layout';
import { BACKGROUND_SIZE, OVERFLOW, VERTICAL_ALIGN } from '../utils/constants';
import { stylesToPixiTextStyles } from '../utils/helpers';
import { PixiTextStyle } from '../utils/text';

import type { ConditionalStyles, GradeToOne, Styles } from '../utils/types';

/** Style controller manages {@link LayoutSystem} styles. */
export class StyleController
{
    protected layout: LayoutSystem;

    protected styles: Styles = {};

    /** Holds all text related styles. This is to be nested by children */
    protected _textStyle: Partial<PixiTextStyle> = {}; // this is to be nested by children

    /** Stores default styles. */
    protected defaultStyles: Styles;

    /** Conditional styles */
    protected conditionalStyles: ConditionalStyles = {};

    /**
     * Manages and sets all the styles of {@link LayoutSystem}
     * @param layout - {@link LayoutSystem} to be styled
     * @param styles - styles to be applied
     */
    constructor(layout: LayoutSystem, styles?: Styles)
    {
        this.layout = layout;
        this.set(styles);
    }

    /**
     * Applies a list of styles for the layout.
     * @param { Styles } styles - styles to be applied
     */
    set(styles?: Styles & ConditionalStyles)
    {
        this.styles.overflow
            = styles?.overflow ?? this.styles.overflow ?? OVERFLOW[0];
        this.styles.display
            = styles?.display ?? this.styles.display ?? 'inline-block';
        this.styles.borderRadius
            = styles?.borderRadius ?? this.styles.borderRadius ?? 0;

        this.styles.zIndex = styles?.zIndex ?? this.styles.zIndex ?? 1;

        this.opacity = styles?.opacity ?? this.styles.opacity ?? 1;

        this.styles.maxWidth = styles?.maxWidth ?? this.styles.maxWidth;
        this.styles.maxHeight = styles?.maxHeight ?? this.styles.maxHeight;

        this.styles.minWidth = styles?.minWidth ?? this.styles.minWidth;
        this.styles.minHeight = styles?.minHeight ?? this.styles.minHeight;

        this.styles.padding = styles?.padding ?? this.styles.padding ?? 0;
        this.styles.paddingTop
            = styles?.paddingTop
            ?? styles?.padding
            ?? this.styles.paddingTop
            ?? 0;
        this.styles.paddingRight
            = styles?.paddingRight
            ?? styles?.padding
            ?? this.styles.paddingRight
            ?? 0;
        this.styles.paddingBottom
            = styles?.paddingBottom
            ?? styles?.padding
            ?? this.styles.paddingBottom
            ?? 0;
        this.styles.paddingLeft
            = styles?.paddingLeft
            ?? styles?.padding
            ?? this.styles.paddingLeft
            ?? 0;

        this.styles.margin = styles?.margin ?? this.styles.margin ?? 0;
        this.styles.marginTop
            = styles?.marginTop ?? styles?.margin ?? this.styles.marginTop ?? 0;
        this.styles.marginRight
            = styles?.marginRight
            ?? styles?.margin
            ?? this.styles.marginRight
            ?? 0;
        this.styles.marginBottom
            = styles?.marginBottom
            ?? styles?.margin
            ?? this.styles.marginBottom
            ?? 0;
        this.styles.marginLeft
            = styles?.marginLeft ?? styles?.margin ?? this.styles.marginLeft ?? 0;

        this.styles.scale = styles?.scale ?? this.styles.scale ?? 1;
        this.styles.scaleX
            = styles?.scaleX ?? styles?.scale ?? this.styles.scaleX ?? 1;
        this.styles.scaleY
            = styles?.scaleY ?? styles?.scale ?? this.styles.scaleY ?? 1;

        this.styles.width = styles?.width ?? this.styles.width ?? 'auto';
        this.styles.height = styles?.height ?? this.styles.height ?? 'auto';

        this.styles.wordWrap = styles?.wordWrap ?? false;

        if (styles?.anchorX !== undefined)
        {
            this.styles.anchorX = styles.anchorX;
        }
        else if (styles?.anchor !== undefined)
        {
            if (typeof styles.anchor === 'number')
            {
                this.styles.anchorX = styles.anchor;
            }
            else if (Array.isArray(styles.anchor))
            {
                this.styles.anchorX = styles.anchor[0];
            }
        }

        if (styles?.anchorY !== undefined)
        {
            this.styles.anchorY = styles.anchorY;
        }
        else if (styles?.anchor !== undefined)
        {
            if (typeof styles.anchor === 'number')
            {
                this.styles.anchorY = styles.anchor;
            }
            else if (
                Array.isArray(styles.anchor)
                && styles.anchor[1] !== undefined
            )
            {
                this.styles.anchorY = styles.anchor[1];
            }
        }

        this.styles.background
            = styles?.background
            ?? styles?.backgroundColor
            ?? this.styles.background;

        this.styles.backgroundSize
            = styles?.backgroundSize
            ?? this.styles.backgroundSize
            ?? BACKGROUND_SIZE[0];

        if (
            this.styles.background instanceof NineSliceSprite
            || this.styles.background instanceof TilingSprite
        )
        {
            this.styles.backgroundSize = 'stretch';
        }

        this.styles.textAlign = styles?.textAlign ?? this.styles.textAlign;
        this.styles.position = styles?.position ?? this.styles.position;
        this.styles.verticalAlign
            = styles?.verticalAlign
            ?? this.styles.verticalAlign
            ?? VERTICAL_ALIGN[0];

        this.styles.aspectRatio
            = styles?.aspectRatio ?? this.styles.aspectRatio ?? 'static';

        this.styles.visible = styles?.visible ?? this.styles.visible ?? true;
        this.visible = this.styles.visible;

        this._textStyle = stylesToPixiTextStyles(styles);

        if (styles)
        {
            this.separateConditionalStyles(styles);
        }
    }

    /**
     * Returns a style value by name.
     * @param style - name of the style
     */
    get(style: keyof Styles): Styles[keyof Styles]
    {
        return this.styles[style];
    }

    /** Returns all styles of the Layout */
    getAll(): Styles
    {
        return this.styles;
    }

    /** Returns all pixi text related styles of the Layout */
    get textStyle(): Partial<PixiTextStyle>
    {
        return this._textStyle;
    }

    /** Sets the opacity of the layout */
    set opacity(value: GradeToOne)
    {
        this.styles.opacity = value;
        this.layout.container.alpha = value;
    }

    /** Returns the opacity of the layout */
    get opacity(): GradeToOne
    {
        return this.styles.opacity;
    }

    /** Set visibility of the layout */
    set visible(value: boolean)
    {
        this.layout.container.visible = value;
    }

    /** Returns visibility of the layout */
    get visible(): boolean
    {
        return this.layout.container.visible;
    }

    /** Checks and applies conditional styles basing on parent size */
    applyConditionalStyles()
    {
        if (!this.hasConditionalStyles) return;

        this.set({
            ...this.defaultStyles,
            ...(this.layout.isRootLayoutPortrait
                ? this.conditionalStyles.portrait
                : this.conditionalStyles.landscape),
        });
    }

    /**
     * Separates conditional styles from default styles
     * @param styles - mixed styles
     */
    protected separateConditionalStyles(styles?: Styles & ConditionalStyles)
    {
        if (!styles.portrait && !styles.landscape)
        {
            this.defaultStyles = {
                ...styles,
            };

            return;
        }

        if (styles.portrait)
        {
            this.conditionalStyles.portrait = {
                ...this.conditionalStyles.portrait,
                ...styles.portrait,
            };
        }

        if (styles.landscape)
        {
            this.conditionalStyles.landscape = {
                ...this.conditionalStyles.landscape,
                ...styles.landscape,
            };
        }

        delete styles.portrait;
        delete styles.landscape;

        this.defaultStyles = styles;
    }

    /** Returns true if there are conditional styles */
    get hasConditionalStyles(): boolean
    {
        return Object.keys(this.conditionalStyles).length > 0;
    }
}

'layout/src/Layout.ts'
:import { Container } from 'pixi.js';
import { AlignController } from './controllers/AlignController';
import { ContentController } from './controllers/ContentController';
import { SizeController } from './controllers/SizeController';
import { StyleController } from './controllers/StyleController';
import { PixiTextStyle } from './utils/text';
import { ConditionalStyles, Content, LayoutOptions, Styles } from './utils/types';

/**
 * Layout controller class for any PixiJS Container based instance.
 *
 * To be be used for automatic align and resize children tree,
 * where every child behavior can be configured using css like configurations.
 *
 * Also it adds a list of css-like properties for styling like background style or text style,
 * check {@link SizeController} class.
 *
 * Any PixiJS Container based instance can be turned into a layout by calling {@link Layout#initLayout} method.
 * @example
 * const container = new Container().initLayout();
 *
 * container.layout?.setStyles({
 *      background: 'black',
 *      width: '100%',
 *      height: '100%',
 *      padding: 10,
 *      overflow: 'hidden',
 *      color: 'white',
 * }); // set styles
 *
 * container.layout?.setContent({
 *      text: 'Hello World',
 * }); // set content
 *
 * Or alternatively:
 *
 * const layoutSystem = new LayoutSystem({
 *      id: 'root',
 *      content: 'Hello World',
 *      styles: {
 *          background: 'black',
 *      }
 * }); // create layout system
 *
 * app.stage.addChild(layoutSystem.container); // add layout system generated container to the stage
 */
export class LayoutSystem
{
    /** Container for all layout children. */
    container: Container;

    /** ID of layout, can be used to set styles in the globalStyles object somewhere higher in hierarchal tree. */
    id: string;

    /** {@link SizeController} is a class for controlling layout and all it's children sizes. */
    size: SizeController;

    /** {@link AlignController} is a class for controlling layout and all it's children alignment. */
    align: AlignController;

    /** {@link StyleController} is a class for controlling styles. */
    protected _style: StyleController;

    /** {@link ContentController} controller is a class for controlling layouts children. */
    content: ContentController;

    /** Stores isPortrait state */
    isPortrait: boolean;

    /**
     * Creates layout system instance.
     * @param options - Layout options
     * @param options.id - ID of the layout.
     * @param options.styles - Styles of the layout. List of available styles can be found in {@link StyleController}.
     * @param options.content - Content of the layout.
     * @param options.globalStyles - Global styles for layout and it's children.
     * @param container - Container for all layout children, will be created if not provided.
     */
    constructor(options?: LayoutOptions, container?: Container)
    {
        this.container = container || new Container();

        this.id = options?.id;

        if (options?.globalStyles)
        {
            // check if there is a global style for this layout
            const styles = options.globalStyles[this.id];

            if (styles && options.styles)
            {
                options.styles = { ...styles, ...options.styles };
            }
            else if (styles)
            {
                options.styles = styles;
            }
        }

        // order here is important as controllers are dependent on each other
        this.size = new SizeController(this);
        this._style = new StyleController(this, options?.styles);
        this.align = new AlignController(this);
        this.content = new ContentController(
            this,
            options?.content,
            options?.globalStyles
        );
    }

    /**
     * Resize method should be called on every parent size change.
     * @param parentWidth
     * @param parentHeight
     */
    resize(parentWidth?: number, parentHeight?: number)
    {
        const width = parentWidth || this.contentWidth || this.size.parentWidth;
        const height
            = parentHeight || this.contentHeight || this.size.parentHeight;

        this.isPortrait = width < height;

        this._style.applyConditionalStyles();
        this.size.resize(parentWidth, parentHeight);
    }

    /** Recalculate positions and sizes of layouts three. */
    refresh()
    {
        this.resize(this.size.parentWidth, this.size.parentHeight);
    }

    /** Returns with of the container */
    get contentWidth(): number | undefined
    {
        if (!this.container?.parent)
        {
            return undefined;
        }

        return this.container.parent.width;
    }

    /** Returns height of the container */
    get contentHeight(): number | undefined
    {
        if (!this.container?.parent)
        {
            return undefined;
        }

        return this.container.parent.height;
    }

    /** Sets the width of layout.  */
    set width(value: number)
    {
        this.size.width = value;
    }

    /** Gets the width of layout. */
    get width()
    {
        return this.size.width;
    }

    /** Sets the height of layout. */
    set height(value: number)
    {
        this.size.height = value;
    }

    /** Gets the height of layout. */
    get height()
    {
        return this.size.height;
    }

    /**
     * Adds content to the layout and reposition/resize other elements and the layout basing on styles.
     * @param {Content} content - Content to be added. Can be string, Container, Layout, LayoutOptions or array of those.
     * Also content can be an object with inner layout ids as a keys, and Content as values.
     */
    addContent(content: Content)
    {
        this.content.createContent(content);
        this.updateParents();
    }

    /**
     * Removes content of the layout by its id and reposition/resize other elements and the layout basing on styles.
     * @param {string} id - id of the content to be removed.
     */
    removeChildByID(id: string)
    {
        this.content.removeContent(id);
    }

    /**
     * Get element from the layout child tree by it's ID
     * @param {string} id - id of the content to be foundS.
     */
    getChildByID(id: string): Layout | Container | undefined
    {
        return this.content.getByID(id);
    }

    /**
     * This is used in case if layout or some of it's children was changed
     * and we need to update sizes and positions for all the parents tree.
     */
    updateParents()
    {
        const rootLayout = this.getRootLayout();

        rootLayout.size.resize();
    }

    /** Returns root layout of the layout tree. */
    getRootLayout(): LayoutSystem
    {
        if (this.container.parent?.layout)
        {
            return this.container.parent.layout.getRootLayout();
        }

        return this;
    }

    /**
     * Updates the layout styles and resize/reposition it and its children basing on new styles.
     * @param styles
     */
    setStyles(styles: Styles & ConditionalStyles)
    {
        this._style.set(styles);
        this.updateParents();
    }

    /** Layout text styles. */
    get textStyle(): Partial<PixiTextStyle>
    {
        return this._style.textStyle;
    }

    /** Layout styles. */
    get style(): Styles
    {
        return this._style.getAll();
    }

    /** Returns true if root layout is in landscape mode. */
    get isRootLayoutPortrait(): boolean
    {
        return this.getRootLayout().isPortrait === true;
    }
}

/**
 * Container with layout system initiated.
 * @example
 *
 * const layout = new Layout({
 * 	styles: {
 * 		width: 100,
 * 		height: 100,
 * 		background: 'red',
 * 	},
 * 	content: [
 * 		'Hello world',
 * 		{
 * 			id: 'innerLayout1',
 * 			text: 'Inner layout 1',
 * 		},
 * 		{
 * 			id: 'innerLayout2',
 * 			text: 'Inner layout 2',
 * 		},
 * 	],
 * 	globalStyles: {
 * 		innerLayout1: {
 * 			width: 200,
 * 			height: 200,
 * 		},
 * 		innerLayout1: {
 * 			width: 200,
 * 			height: 200,
 * 		},
 * 	},
 * });
 */
export class Layout extends Container
{
    override layout: LayoutSystem;

    /**
     * Creates layout container.
     * @param options
     */
    constructor(options?: LayoutOptions)
    {
        super();

        this.layout = new LayoutSystem(options, this);
    }

    /** Get {@link SizeController} */
    get size(): SizeController
    {
        return this.layout.size;
    }

    /** {@link AlignController} */
    get align(): AlignController
    {
        return this.layout.align;
    }

    /** {@link ContentController} */
    get content(): ContentController
    {
        return this.layout.content;
    }

    /** ID of layout, can be used to set styles in the globalStyles. */
    get id()
    {
        return this.layout.id;
    }

    /** ID of layout, can be used to set styles in the globalStyles. */
    set id(value: string)
    {
        this.layout.id = value;
    }

    /** Returns with of the layouts content. */
    get contentWidth(): number | undefined
    {
        return this.layout.contentWidth;
    }

    /** Returns height of the layouts content. */
    get contentHeight(): number | undefined
    {
        return this.layout.contentHeight;
    }

    /** Set the width of layout.  */
    override set width(value: number)
    {
        this.layout.width = value;
    }

    /** Get the width of layout. */
    override get width()
    {
        return this.layout.width;
    }

    /** Set the height of layout. */
    override set height(value: number)
    {
        this.layout.height = value;
    }

    /** Get the height of layout. */
    override get height()
    {
        return this.layout.height;
    }

    /**
     * Add content to the layout system and reposition/resize elements basing on styles.
     * @param {Content} content - Content to be added. Can be string, Container, Layout, LayoutOptions or array of those.
     * Also content can be an object where keys are ids of child layouts to create, and Content as values.
     */
    addContent(content: Content)
    {
        this.layout.addContent(content);
    }

    /**
     * Remove content from layout system by its id and reposition/resize elements basing on styles.
     * @param {string} id - id of the content to be removed.
     */
    removeChildByID(id: string)
    {
        this.layout.removeChildByID(id);
    }

    /**
     * Get element from the layout system children tree by it's ID
     * @param {string} id - id of the content to be foundS.
     */
    getChildByID(id: string): Layout | Container | undefined
    {
        return this.layout.getChildByID(id);
    }

    /**
     * Updates the layout styles and resize/reposition elements basing on new styles.
     * @param styles
     */
    setStyles(styles: Styles)
    {
        this.layout.setStyles(styles);
    }

    /** Layout text styles. */
    get textStyle(): Partial<PixiTextStyle>
    {
        return this.layout.textStyle;
    }

    /** Layout styles. */
    get style(): Styles
    {
        return this.layout.style;
    }

    /**
     * Resize method should be called on every parent size change.
     * @param parentWidth
     * @param parentHeight
     */
    resize(parentWidth?: number, parentHeight?: number)
    {
        this.layout.resize(parentWidth, parentHeight);
    }

    /** Recalculate positions and sizes of layouts three. */
    refresh()
    {
        this.resize(this.size.parentWidth, this.size.parentHeight);
    }
}

declare module 'pixi.js'
{
    interface Container
    {
        initLayout(config?: LayoutOptions): Container;
        layout?: LayoutSystem;
        isPixiLayout?: boolean;
    }
}

if (!Container.prototype.initLayout)
{
    Object.defineProperty(Container.prototype, 'initLayout', {
        value(options?: LayoutOptions): void
        {
            if (!this.layout)
            {
                this.layout = new LayoutSystem(options, this);
                this.isPixiLayout = true;
            }

            return this;
        },
    });
}

'layout/src/stories/basic/TransformContainer.stories.ts'
:import { Container } from 'pixi.js';
import { Layout } from '../../Layout';
import {
    ALIGN,
    CSS_COLOR_NAMES,
    LOREM_TEXT,
    POSITION,
} from '../../utils/constants';
import { toolTip } from '../components/ToolTip';
import { argTypes, getDefaultArgs } from '../utils/argTypes';

const color = Object.keys(CSS_COLOR_NAMES).map((key) => key);

const args = {
    color,
    backgroundColor: '#000000',
    width: 50,
    height: 50,
    padding: 15,
    opacity: 1,
    fontSize: 24,
    borderRadius: 20,
    textAlign: ALIGN,
    overflow: ['hidden', 'visible'],
    position: POSITION,
    wordWrap: true,
};

class LayoutStory
{
    private toolTip: Layout;
    private layoutContainer: Container;
    view: Container;
    w: number;
    h: number;

    constructor({
        color,
        backgroundColor,
        width,
        height,
        padding,
        opacity,
        overflow,
        fontSize,
        borderRadius,
        textAlign,
        position,
        wordWrap,
    }: any)
    {
        this.view = new Container();

        this.addTooltip(
            `Shows you how to turn any Container into a Layout.\n`
                + `Same works for any other Container based element like Sprite or Graphics.\n`
        );

        this.layoutContainer = new Container().initLayout();

        this.view.addChild(this.layoutContainer);

        this.layoutContainer.layout?.setStyles({
            background: backgroundColor,
            width: `${width}%`,
            height: `${height}%`,
            padding,
            opacity,
            overflow,
            // text options
            color,
            textAlign,
            fontSize,
            position,
            borderRadius,
            wordWrap,
        });

        this.layoutContainer.layout?.addContent(LOREM_TEXT);
    }

    async addTooltip(text: string)
    {
        this.toolTip = await toolTip(text);
        this.view.addChild(this.toolTip);
        this.toolTip.resize(this.w, this.h);
    }

    resize(w: number, h: number)
    {
        this.w = w;
        this.h = h;

        this.layoutContainer?.layout?.resize(w, h);
        this.toolTip?.resize(w, h);
    }
}

export const TransformContainer = (params: any) => new LayoutStory(params);

export default {
    title: 'Basic',
    argTypes: argTypes(args),
    args: getDefaultArgs(args),
};

