        const page1 = await client.query(queryFn({ size: 2 }))

        expect(page1.data[0].data.foo).toBe("D")
        expect(page1.data[1].data.foo).toBe("C")

        const page2 = await client.query(
            queryFn({ size: 2, after: page1.after })
