'logseq-datenlp-plugin/src/features/toolbar/helpers.ts'
:import { PageEntity } from '@logseq/libs/dist/LSPlugin'
import {
  addDays,
  Day,
  eachDayOfInterval,
  endOfWeek,
  parse,
  setWeek,
  startOfWeek,
  subDays,
} from 'date-fns'
import { getDateForPageWithoutBrackets } from 'logseq-dateutils'

type StartDayOfWeek = 'Monday' | 'Saturday' | 'Sunday'

const startOfWeekMap: Record<StartDayOfWeek, Day> = {
  Monday: 1,
  Sunday: 0,
  Saturday: 6,
}

const getJournalDay = async () => {
  const currPage = await logseq.Editor.getCurrentPage()
  if (!currPage || !currPage['journal?']) {
    return new Date()
  }

  const { journalDay } = currPage as PageEntity
  if (!journalDay)
    throw new Error(
      'Something is wrong. This page is supposed to be a journal page.',
    )

  const parsedJournalDay = parse(journalDay.toString(), 'yyyyMMdd', new Date())
  return parsedJournalDay
}

export const helpers = {
  previousDayName: async () => {
    const parsedJournalDay = await getJournalDay()
    if (!parsedJournalDay) return

    const previousDay = subDays(parsedJournalDay, 1)
    const { preferredDateFormat } = await logseq.App.getUserConfigs()
    return getDateForPageWithoutBrackets(previousDay, preferredDateFormat)
  },
  nextDayName: async () => {
    const parsedJournalDay = await getJournalDay()
    if (!parsedJournalDay) return

    const nextDay = addDays(parsedJournalDay, 1)
    const { preferredDateFormat } = await logseq.App.getUserConfigs()
    return getDateForPageWithoutBrackets(nextDay, preferredDateFormat)
  },
  disDayName: async () => {
    const { preferredDateFormat } = await logseq.App.getUserConfigs()
    return getDateForPageWithoutBrackets(new Date(), preferredDateFormat)
  },
  insertDaysInWeek: async (year: number, weekNumber: number) => {
    const dateInWeek = setWeek(new Date(year, 0, 1), weekNumber)
    const startDay = logseq.settings!.startOfWeek as StartDayOfWeek
    const weekStartsOn = startOfWeekMap[startDay]
    const startDate = startOfWeek(dateInWeek, { weekStartsOn })
    const endDate = endOfWeek(dateInWeek, { weekStartsOn })
    const dates = eachDayOfInterval({ start: startDate, end: endDate })
    const { preferredDateFormat } = await logseq.App.getUserConfigs()

    return dates.map((date) =>
      getDateForPageWithoutBrackets(date, preferredDateFormat),
    )
  },
}

'logseq-datenlp-plugin/src/features/toolbar/handle-append-page-embeds.ts'
:import { helpers } from './helpers'

export const handleAppendEmbeds = async (
  pageName: string,
  year: number,
  week: number,
) => {
  const pbt = await logseq.Editor.getPageBlocksTree(pageName)
  if (pbt.length === 0 || pbt.length === 1) {
    const dateArr = await helpers.insertDaysInWeek(year, week)
    dateArr.forEach(
      async (date) =>
        await logseq.Editor.appendBlockInPage(
          pageName,
          `{{embed [[${date}]]}}`,
        ),
    )
    await logseq.UI.showMsg(
      'Appended dates for the week as page embeds',
      'success',
    )
  }
  await logseq.Editor.exitEditingMode(false)
}

'logseq-datenlp-plugin/src/features/toolbar/index.ts'
:import { getWeek, getYear } from 'date-fns'

import { handleAppendEmbeds } from './handle-append-page-embeds'
import { helpers } from './helpers'
import css from './toolbar.css?raw'

export const handleToolbar = async () => {
  logseq.provideStyle(css)

  logseq.provideModel({
    async previousDay() {
      logseq.App.pushState('page', {
        name: await helpers.previousDayName(),
      })
    },
    async nextDay() {
      logseq.App.pushState('page', {
        name: await helpers.nextDayName(),
      })
    },
    async disDay() {
      logseq.App.pushState('page', {
        name: await helpers.disDayName(),
      })
    },
    async showWeek() {
      const year = getYear(new Date())
      const week = getWeek(new Date())
      const pageName = `${year}/Week ${week}`
      await logseq.Editor.createPage(
        pageName,
        {},
        {
          redirect: false,
          createFirstBlock: false,
          journal: false,
        },
      )

      // Create the page embeds
      await handleAppendEmbeds(pageName, year, week)

      // Go to page
      logseq.App.pushState('page', {
        name: pageName,
      })
    },
  })

  logseq.App.registerUIItem('toolbar', {
    key: 'datenlp-day-forward',
    template: `<a class="button datenlp-toolbar" data-on-click="nextDay"><i class="ti ti-chevron-right"></i></a>`,
  })
  logseq.App.registerUIItem('toolbar', {
    key: 'datenlp-day-dis', // have to use slang as logseq sorts the toolbar by name
    template: `<a class="button datenlp-toolbar" data-on-click="disDay">Today</a>`,
  })
  logseq.App.registerUIItem('toolbar', {
    key: 'datenlp-day-back',
    template: `<a class="button datenlp-toolbar" data-on-click="previousDay"><i class="ti ti-chevron-left"></i></a>`,
  })
  logseq.App.registerUIItem('toolbar', {
    key: 'datenlp-week-dis',
    template: `<a class="button datenlp-toolbar" data-on-click="showWeek">Week ${getWeek(new Date())}</a>`,
  })
}

