import { useState, useEffect, useRef } from 'react'
import { ChatGPTProps, ChatMessage, ChatRole } from './interface'

#Modify
export const useChatGPT = (props: ChatGPTProps) => {
  const [loading, setLoading] = useState(false)
  const [disabled, setDisabled] = useState(false)
  const [messages, setMessages] = useState<ChatMessage[]>([])
  const currentMessage = useRef<string>('')

  const onSend = async (message: ChatMessage) => {
    setMessages(prev => [...prev, message])
    setLoading(true)
    setDisabled(true)

    const response = await fetch(props.fetchPath, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(message)
    })

    const reader = response.body.getReader()
    const decoder = new TextDecoder('utf-8')
    let buffer = ''

    while (true) {
      const { done, value } = await reader.read()
      if (done) {
        break
      }

      buffer += decoder.decode(value, { stream: true })
      currentMessage.current = buffer
    }

    setMessages(prev => [...prev, { content: buffer, role: ChatRole.Assistant }])
    currentMessage.current = ''
    setLoading(false)
    setDisabled(false)
  }

  const onClear = () => {
    setMessages([])
  }

  const onStop = () => {
    setLoading(false)
    setDisabled(false)
  }

  return {
    loading,
    disabled,
    messages,
    currentMessage,
    onSend,
    onClear,
    onStop
  }
}

