export class OutlierDetectionService {
  public detectOutliers(values: Array<Number>): Array<Number> {
    let mean = datalib.mean(values);
    let stdev = datalib.stdev(values);
    let outliers = values.filter(value => Math.abs(value - mean) > 2 * stdev);
    return outliers;
  }
}

