'angular-2-navbar/navbar.service.ts'
:import { Injectable, Injector } from '@angular/core';

export interface NavbarItem {
  route: string;
  label: string;
  weight?: number;
  canDisplay?: Function[];
}

export type NavbarItems = NavbarItem[];

@Injectable()
export class NavbarService {

  constructor(private items: NavbarItems, private injector: Injector) {
    this.items = this.items.map((item) => {
      if (item.weight === undefined) {
        item.weight = 0;
      }
      return item;
    });
  }

  getNavbarItems() {
    const canDisplay = (item) => {
      if (!item.canDisplay || !item.canDisplay.length) {
        return true;
      }
      return item.canDisplay.filter((guard) => this.injector.get(guard).canActivate(item)).length;
    };
    return this.items
      .filter((item) => canDisplay(item))
      .sort((a: NavbarItem, b: NavbarItem) => {
        if (a.weight === b.weight) {
          return 0;
        }
        if (a.weight < b.weight) {
          return -1;
        }
        return 1;
      });
  }

}


'angular-2-navbar/navbar.component.ts'
:import { Component, OnInit, ContentChild, ViewChild, ViewContainerRef, TemplateRef, DoCheck } from '@angular/core';
import { NavbarService, NavbarItems } from './navbar.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent implements OnInit, DoCheck {

  private items: NavbarItems;

  @ContentChild(TemplateRef) customTemplate: TemplateRef<Object>;
  @ViewChild(TemplateRef) defaultTemplate: TemplateRef<Object>;

  constructor(private viewContainer: ViewContainerRef,
              private navbarService: NavbarService) {
   }

  ngOnInit() {
    this.items = this.navbarService.getNavbarItems();
  }

  ngDoCheck() {

    if (!this.defaultTemplate && !this.customTemplate) {
      return;
    }

    this.viewContainer.clear();

    if (this.customTemplate) {
      this.viewContainer.createEmbeddedView.call(this.viewContainer, this.customTemplate, {
        items: this.items
      });
    } else {
      this.viewContainer.createEmbeddedView.call(this.viewContainer, this.defaultTemplate, {
        items: this.items
      });
    }
  }

}

'angular-2-navbar/navbar.module.ts'
:import { NgModule, ModuleWithProviders, Injector, OpaqueToken } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { NavbarComponent } from './navbar.component';
import { NavbarService, NavbarItems } from './navbar.service';

const NAVBAR_ITEMS = new OpaqueToken('NAVBAR_ITEMS');

function navbarServiceFactory(itemsCollection: NavbarItems[], injector: Injector) {
  let allItems = itemsCollection.reduce((aggregate, items) => aggregate.concat(items), []);
  return new NavbarService(allItems, injector);
}

export function provideItems(items: NavbarItems): any {
  return [
    {provide: NAVBAR_ITEMS, multi: true, useValue: items}
  ];
}

@NgModule({
  imports: [
    CommonModule,
    RouterModule
  ],
  exports: [
    NavbarComponent
  ],
  declarations: [NavbarComponent]
})
export class NavbarModule {

  static forChild(items: NavbarItems): ModuleWithProviders {
    const providers = provideItems(items);
    return {ngModule: NavbarModule, providers: [providers]};
  }

  static forRoot(items: NavbarItems = []): ModuleWithProviders {

    let providers = [{
      provide: NavbarService,
      useFactory: navbarServiceFactory,
      deps: [NAVBAR_ITEMS, Injector]
    }];

    if (items.length) {
      providers.push(provideItems(items));
    }

    return {
      ngModule: NavbarModule,
      providers: providers
    };
  }

  constructor(private navbarService: NavbarService) {}

}

