:import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import { Avatar, Box, Typography } from '@material-ui/core';
import React, { memo } from 'react';
import Divider from '@material-ui/core/Divider';
import Tooltip from '@material-ui/core/Tooltip/Tooltip';
import { WarningOutlined } from '@material-ui/icons';

import { textOverflow } from '../../utils/stringUtils';
import { percentIt } from '../../utils/mathUtils';
import { EnhancedPlayer } from '../../store/stats/statsRedux';

import CrewAvatar from './avatar/CrewAvatar';

const useStyles = makeStyles({
  subtitle: {
    display: 'inline-flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
});

interface Props {
  player: EnhancedPlayer;
  placement: number;
}

function PlayerStatsItem({ player, placement }: Props): JSX.Element {
  const classes = useStyles();

  return (
    <Grid container item xs={12} md={6}>
      <Grid item xs={2}>
        <Box height="100%" display="flex" justifyContent="center" alignItems="center" mr={1}>
          <Avatar>{ordinalSuffix(placement)}</Avatar>
        </Box>
      </Grid>
      <Grid container item xs={10}>
        <Grid item xs={12}>
          <Typography variant="subtitle1">
            <Box display="inline-flex" alignItems="center">
              {player.name === 'Unknown player' && (
                <Tooltip title="Some data has been corrupted, please send a bug report!">
                  <WarningOutlined color="secondary" />
                </Tooltip>
              )}
            </Box>{' '}
            {textOverflow(player.name, 10)},{' '}
            <Box display="inline" fontWeight="bold">
              {percentIt(player.winRates.total)}
            </Box>{' '}
            of games won
          </Typography>
        </Grid>
        <Grid item xs={8}>
          <Tooltip title="Win rate as specific role">
            <Typography variant="subtitle2" className={classes.subtitle}>
              <CrewAvatar type="impostor" inline />
              {percentIt(player.winRates.impostor)}
              <CrewAvatar type="crew" inline />
              {percentIt(player.winRates.crew)}
            </Typography>
          </Tooltip>
        </Grid>
        <Grid item xs={4}>
          <Tooltip title="Percent of rounds player was impostor">
            <Typography variant="caption">{percentIt(player.impostorRate)} impostor</Typography>
          </Tooltip>
        </Grid>
      </Grid>
      <Grid item xs={12}>
        <Box mt={0.5}>
          <Divider />
        </Box>
      </Grid>
    </Grid>
  );
}

function ordinalSuffix(i: number): string {
  const j = i % 10;
  const k = i % 100;

  if (j == 1 && k != 11) {
    return i + 'st';
  } else if (j == 2 && k != 12) {
    return i + 'nd';
  } else if (j == 3 && k != 13) {
    return i + 'rd';
  }
  return i + 'th';
}

export default memo(PlayerStatsItem);

'among-us-stats/src/components/shared/SummaryCard.tsx'
:import React, { useRef, Suspense } from 'react';
import GamepadIcon from '@material-ui/icons/Gamepad';
import Paper from '@material-ui/core/Paper';
import WarningIcon from '@material-ui/icons/Warning';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Tooltip from '@material-ui/core/Tooltip';
import { useSelector } from 'react-redux';

import { EnhancedPlayer, Session } from '../../store/stats/statsRedux';
import { formatDistanceToNow, parseIso } from '../../utils/dateUtils';
import { RootState } from '../../store/redux';

import PlayerStatsItem from './PlayerStatsItem';
import IconInfoText from './IconInfoText';
import Scoreboard from './Scoreboard';
import EditableTitle from './EditableTitle';

const LazyDiscordShareButton = React.lazy(() => import('./DiscordShareButton'));

const useStyles = makeStyles((theme) => ({
  rootBox: {
    position: 'relative',
  },
  gamepadIcon: {
    marginRight: theme.spacing(1),
  },
  autoPad: {
    flex: '1 1 auto',
  },
  optionalActions: {
    marginRight: theme.spacing(2),
    '& > button': {
      marginLeft: theme.spacing(2),
    },
  },
  shareButtonPlaceholder: {
    minWidth: theme.spacing(12),
  },
  gamesPlayed: {
    position: 'absolute',
    bottom: 0,
    left: 0,
  },
  timestamp: {
    position: 'absolute',
    bottom: 0,
    right: 0,
  },
}));

interface Props {
  session: Session<EnhancedPlayer>;
  extraActions?: JSX.Element | JSX.Element[];
  noTimestamp?: boolean;
}

function SummaryCard({ session, extraActions, noTimestamp = false }: Props): JSX.Element {
  const classes = useStyles();
  const summaryCardRef = useRef<HTMLDivElement>(null);
  const settings = useSelector((state: RootState) => state.settings);
  const showDiscordShareButton = settings.discordShareWebhook && !extraActions;

  const playersByWinRate = [...session.players].sort((a, b) => b.winRates.total - a.winRates.total);
  const unfinishedGames = session.games.filter((it) => it.winner == null).length;

  return (
    <div>
      <div ref={summaryCardRef}>
        <Box className={classes.rootBox}>
          <Box pt={1} pl={2} display="flex" alignItems="center">
            <GamepadIcon className={classes.gamepadIcon} />
            <EditableTitle session={session} />
            <Box className={classes.autoPad} />
            <Box className={classes.optionalActions}>{extraActions}</Box>
            {showDiscordShareButton && <Box className={classes.shareButtonPlaceholder} />}
          </Box>
          {unfinishedGames > 0 && (
            <Box m={2}>
              <Paper elevation={6} variant="outlined">
                <Box>
                  <IconInfoText
                    text={`There are ${unfinishedGames} unfinished games. Statistics will be incomplete.`}
                    icon={<WarningIcon />}
                    opacity={1}
                  />
                </Box>
              </Paper>
            </Box>
          )}
          <Box m={1}>
            <Grid container spacing={1}>
              {playersByWinRate.map((it, index) => (
                <PlayerStatsItem key={it.playerId} player={it} placement={index + 1} />
              ))}
            </Grid>
          </Box>
          <Box display="flex" justifyContent="center" pb={4}>
            <Scoreboard session={session} />
          </Box>
          <Box m={2} className={classes.gamesPlayed}>
            <Typography variant="subtitle2">{session.games.length} games played</Typography>
          </Box>
          {!noTimestamp && (
            <Box m={2} className={classes.timestamp}>
              <Tooltip interactive title={session.lastGamePlayed}>
                <Typography variant="subtitle2">{formatDistanceToNow(parseIso(session.lastGamePlayed))} ago</Typography>
              </Tooltip>
            </Box>
          )}
        </Box>
      </div>
      <Suspense fallback={null}>
        {showDiscordShareButton && <LazyDiscordShareButton shareBoxRef={summaryCardRef} />}
      </Suspense>
    </div>
  );
}

export default SummaryCard;

