import { unescape as unEsc } from "html-escaper";

#Modify
export function unescape(str: string, customEntities?: Record<string, string>) {
  let result = unEsc(str);
  if (customEntities) {
    for (const [entity, replacement] of Object.entries(customEntities)) {
      result = result.replaceAll(entity, replacement);
    }
  }
  return result.replaceAll("&#x3C;", "<").replaceAll("&#x26;", "&");
}

