import {Injectable} from '@angular/core';
import {HttpClient} from '@angular/common/http';
import {Observable} from "rxjs";
import {Person} from "./person";

@Injectable({
    providedIn: 'root',
})
export class PersonService {

    constructor(private http: HttpClient) { }

    findAll(): Observable<Person[]> {
        return this.http.get<Person[]>("/api/persons")
    }

    findById(id: number): Observable<Person> {
        return this.http.get<Person>(`/api/persons/${id}`)
    }

    save(person: Person): Observable<any> {
        // #Modify: Return the saved person's ID
        return this.http.post<{id: number}>("/api/persons", person);
    }
}

import {Component} from '@angular/core';
import {ActivatedRoute, Router} from '@angular/router';
import {MatSnackBar} from '@angular/material/snack-bar';
import {PersonService} from '../person.service';
import {Person} from '../person';

@Component({
    selector: 'app-person-edit',
    templateUrl: './person-edit.component.html',
    styleUrls: ['./person-edit.component.css']
})
export class PersonEditComponent {

    person: Person = new Person();

    constructor(private route: ActivatedRoute, private personService: PersonService, private snackBar: MatSnackBar, private router: Router) { }

    ngOnInit(): void {
        this.getPerson();
    }

    getPerson(): void {
        const idParam = this.route.snapshot.paramMap.get('id');
        if (idParam === "new") {
            this.person = new Person();
        } else {
            const id = parseInt(idParam as string, 10);
            this.personService.findById(id).subscribe(person => this.person = person);
        }
    }

    save(): void {
        // #Modify: Handle the returned ID
        this.personService.save(this.person).subscribe((response) => {
            let snackBar = this.snackBar.open(`Person saved with ID: ${response.id}`, "OK",);
            snackBar.onAction().subscribe(value => this.router.navigateByUrl("/persons"));
        });
    }
}

